def name_not_empty(cls, v):
    if not v.strip():
        raise ValueError('Template name cannot be empty')
    return v

def system_message_validation(cls, v):
    if not isinstance(v, dict):
        raise ValueError('system_message must be a dictionary')
    required_keys = ['agent_role', 'agent_task', 'agent_goal', 'key_information', 'context_placeholder', 'agent_response', 'output_format']
    missing = [key for key in required_keys if key not in v]
    if missing:
        raise ValueError(f'Missing required system_message components: {missing}')
    return v

def components_validation(cls, values):
    if not values.get("system_message") and not values.get("human_message"):
        raise ValueError("At least one of system_message or human_message must be provided")
    return values

def chain_steps_validation(cls, v):
    if not isinstance(v, list) or len(v) == 0:
        raise ValueError('chain_steps must be a non-empty list')
    for step in v:
        if not isinstance(step, dict):
            raise ValueError('Each chain step must be a dictionary')
        if 'prompt_name' not in step:
            raise ValueError('Each chain step must include prompt_name')
    return v

def input_mapping_validation(cls, v):
    if not isinstance(v, list) or len(v) == 0:
        return v
    for i, step in enumerate(v):
        if i > 0 and 'input_mapping' in step:
            for key, value in step['input_mapping'].items():
                if isinstance(value, str) and value.startswith('$outputs.') and value[9:] not in [s['output_key'] for s in v[:i]]:
                    raise ValueError(f'Input mapping refers to non-existent output key: {value[9:]}')
    return v
